const colors = require('color/safe');

const defaultValues = {
    port: 3000,
};

type configType = typeof defaultValues;

const ConfigByEnv: { [name: string]: configType } = {
    development: {
        ...defaultValues,
    },
    integration: {
        ...defaultValues,
    },
    stage: {
        ...defaultValues,
    },
    production: {
        ...defaultValues,
    },
};

export const getENV = () => {
    if (process.env.NODE_ENV && Object.keys(ConfigByEnv).indexOf(process.env.NODE_ENV || '') > -1) {
        return process.env.NODE_ENV;
    }
    console.log(colors.yellow(`process.env.NODE_ENV unvalid value set development config, process.env.NODE_ENV = ${process.env.NODE_ENV}`));
    return 'development';
};

export default ConfigByEnv[getENV()] as typeof defaultValues;
